#!/usr/bin/env python3\n\"\"\"\nQuick SerpAPI Google Jobs Test\nTest the SerpAPI directly to verify it's working\n\"\"\"\n\nimport requests\nimport json\n\ndef test_serpapi_google_jobs():\n    \"\"\"\n    Test SerpAPI Google Jobs directly\n    \"\"\"\n    api_key = \"a448fc3f98bea2711a110c46c86d75cc09e786b729a8212f666c89d35800429f\"\n    \n    # Test parameters\n    params = {\n        \"engine\": \"google_jobs\",\n        \"q\": \"software engineer India\",\n        \"location\": \"India\", \n        \"hl\": \"en\",\n        \"gl\": \"in\",\n        \"api_key\": api_key\n    }\n    \n    print(\"ğŸ§ª Testing SerpAPI Google Jobs directly...\")\n    print(f\"ğŸ” Search: {params['q']} in {params['location']}\")\n    print(f\"ğŸ”‘ API Key: {api_key[:20]}...\")\n    \n    try:\n        response = requests.get(\"https://serpapi.com/search.json\", params=params, timeout=30)\n        \n        print(f\"ğŸ“¡ Response Status: {response.status_code}\")\n        \n        if response.status_code == 200:\n            data = response.json()\n            \n            # Check for errors\n            if \"error\" in data:\n                print(f\"âŒ SerpAPI Error: {data['error']}\")\n                return False\n            \n            # Check for jobs\n            jobs_results = data.get(\"jobs_results\", [])\n            print(f\"âœ… Found {len(jobs_results)} jobs from Google Jobs API\")\n            \n            # Show first few jobs\n            if jobs_results:\n                print(\"\\nğŸ“‹ Sample Jobs:\")\n                for i, job in enumerate(jobs_results[:3], 1):\n                    print(f\"\\n{i}. {job.get('title', 'No title')}\")\n                    print(f\"   Company: {job.get('company_name', 'No company')}\")\n                    print(f\"   Location: {job.get('location', 'No location')}\")\n                    print(f\"   Via: {job.get('via', 'Direct')}\")\n                    \n                return True\n            else:\n                print(\"âš ï¸ No jobs found in response\")\n                print(f\"ğŸ“„ Full response keys: {list(data.keys())}\")\n                return False\n                \n        else:\n            print(f\"âŒ HTTP Error: {response.status_code}\")\n            print(f\"ğŸ“„ Response: {response.text[:500]}\")\n            return False\n            \n    except Exception as e:\n        print(f\"âŒ Exception: {str(e)}\")\n        return False\n\nif __name__ == \"__main__\":\n    test_serpapi_google_jobs()\n